  def valid_attributes
    {}
  end

  describe "GET index" do
    it "assigns all csv_files as @csv_files" do
      csv_file = CsvFile.create! valid_attributes
      get :index
      assigns(:csv_files).should eq([csv_file])
    end
  end

  describe "GET show" do
    it "assigns the requested csv_file as @csv_file" do
      csv_file = CsvFile.create! valid_attributes
      get :show, :id => csv_file.id.to_s
      assigns(:csv_file).should eq(csv_file)
