  end

  describe "GET new" do
    it "assigns a new csv_file as @csv_file" do
      get :new
      assigns(:csv_file).should be_a_new(CsvFile)
    end
  end

  describe "GET edit" do
    it "assigns the requested csv_file as @csv_file" do
      csv_file = CsvFile.create! valid_attributes
      get :edit, :id => csv_file.id.to_s
      assigns(:csv_file).should eq(csv_file)
    end
  end

  describe "POST create" do
    describe "with valid params" do
      it "creates a new CsvFile" do
        expect {
          post :create, :csv_file => valid_attributes
        }.to change(CsvFile, :count).by(1)
      end

      it "assigns a newly created csv_file as @csv_file" do
        post :create, :csv_file => valid_attributes
        assigns(:csv_file).should be_a(CsvFile)
